import React, { useState } from 'react';
import ScheduleForm from '../components/schedule/ScheduleForm';
import ScheduleList from '../components/schedule/ScheduleList';
import './ScheduleManager.css';

const ScheduleManager = () => {
  const [isAdding, setIsAdding] = useState(false); // State to track if we are adding a new schedule
  const [editingSchedule, setEditingSchedule] = useState(null); // Store the schedule being edited

  const handleAddNewSchedule = () => {
    setIsAdding(true);
    setEditingSchedule(null); // Clear the editing state when adding a new schedule
  };

  const handleEditSchedule = (scheduleId) => {
    setIsAdding(true);
    setEditingSchedule(scheduleId); // Set the schedule to be edited
  };

  const handleCancel = () => {
    setIsAdding(false);
    setEditingSchedule(null); // Clear the editing state
  };

  return (
    <div className="schedule-manager-container">
      <h2>My Schedule</h2>
      <button onClick={handleAddNewSchedule} className="btn btn-primary">
        Add New Schedule
      </button>

      {isAdding && (
        <ScheduleForm
          existingSchedule={editingSchedule} // Pass existing schedule if editing
          onCancel={handleCancel} // Cancel editing/adding
        />
      )}

      <ScheduleList onEdit={handleEditSchedule} />
    </div>
  );
};

export default ScheduleManager;
